muclient.script:
-- fix stupid syntax highlighting in SublimeText --> '
require "aard_lua_extras" require "serialize" dofile(GetInfo(60) .. "aardwolf_colors.lua")
require "addxml" function apply_pattern_as_trigger(original_pattern, nocase, alone) local flags = trigger_flag.Enabled + trigger_flag.OmitFromLog + trigger_flag.OmitFromOutput + trigger_flag.RegularExpression + trigger_flag.Temporary if nocase then flags = flags + trigger_flag.IgnoreCase end addxml.trigger( { match = original_pattern:gsub("([^%%]?)%%([^%%])", "%1\%2"), -- replace % escapes with \ escapes enabled = true, regexp = true, sequence = 100, omit_from_output = true, omit_from_log = true, name = "A"..GetUniqueID()..GetUniqueID(), -- need a name to set group script = "filter_trigger", send_to = sendto.script, send = "StopEvaluatingTriggers(true)", group = "all" } ) end
function set_triggers() DeleteTriggerGroup("all") for i,sub in ipairs(subs) do apply_pattern_as_trigger(sub.original, sub.nocase, sub.alone) end for i,gag in ipairs(gags) do apply_pattern_as_trigger(gag.original, gag.nocase, gag.alone) end end
-- init tables loadstring( GetVariable("subs") or "subs = {}" )() loadstring( GetVariable("gags") or "gags = {}" )()
set_triggers()
function usagesub(line, embedded) if not embedded then Note() end
if not embedded then AnsiNote(ColoursToANSI("You typed: '@W"..line.."@Y'")) tts_warning() Note() end AnsiNote(ColoursToANSI("@YAvailable commands for the #sub/#gag plugin are:".. "
@Y #subs : List all current substitutions".. "
@Y #gags : List all current gags".. "
@Y #sub {A} {B} : Replaces phrase A with phrase B".. "
@Y #unsub #<number> : Removes numbered substitution".. "
@Y #gag {A} : Gag lines with phrase A".. "
@Y #ungag #<number> : Removes numbered gag")) if not embedded then AnsiNote(ColoursToANSI("@Y #sub help : More complete help for this plugin")) end AnsiNote(ColoursToANSI("
@w Note that you must use curly braces around each phrase for demarcation,".. "
@w not quotation marks or anything else.".. "
@w Add @C#alone@w to the line to only match at word or color boundaries.".. "
@w Add @C#nocase@w to the line to do case-insensitive matching.")) if not embedded then Note() end end
function tts_warning() AnsiNote(ColoursToANSI("@cNote: If you are using text to speech, you will need to have enabled".. "
@call punctuation reading to follow the documentation for this plugin.".. "
@cThe plugin uses some symbols for interaction, and the full help includes".. "
@cexample Lua plugin code as well.")) end
function helpsub(name, line, wildcards)
if wildcards[1] ~= "help" and wildcards[1] ~= "#help" then usagesub(line, false) return end
local header = "begin help for the #sub/#gag plugin" AnsiNote(ColoursToANSI("

@Y"..string.rep(" ", (60-#header)/2)..header)) tts_warning() AnsiNote(ColoursToANSI("
@w"..GetPluginInfo(GetPluginID(), 3):gsub("(@%w)", "%1@%1").."
@Y")) usagesub(line, true) AnsiNote(ColoursToANSI( "@w example: @C#sub {hello} {Howdy} #alone #nocase".. "
@w This will match HeLlO but not othello.".. "
@w".. "
@w Parentheses will act as captures. Use '%' to escape literal '(', ')', and '%'.".. "
@w example: @C#sub {(player)} {%1 (rude)} #alone #nocase".. "
@w This will do almost the same things as".. "
@w @C#sub {player} {player (rude)} #alone #nocase".. "
@w but it will preserve the original capitalization.".. "
@w".. "
@w Add @C#function@w to a #sub line to pass matching components to a plugin function".. "
@w instead of doing a direct replacement.".. "
@w For this, your replacement pattern must be @Wplugin_id:function_name@w.".. "
@w example: @C#sub {player} {12345678901234567890abcd:my_example} #alone #function".. "
@w Then in your plugin with ID 12345678901234567890abcd,".. "
@w you would add a new my_example function. It could".. "
@w be something like:".. "
@w@C function my_example(...)".. "
@w@C local args = {...}".. "
@w@C if my_plugin_variable == 100 then".. "
@w@C -- returning a string says replace with this".. "
@w@C return args[1].." (@@Rbutternut squash$C)"".. "
@w@C else".. "
@w@C -- returning false or nil says don't do a replacement".. "
@w@C return false".. "
@w@C end".. "
@w@C end".. "
@w Since gags don't normally have a replacement pattern field, adding one".. "
@w will make them behave similarly.".. "
@w There is no need to add #function for gags.".. "
@w example: @C#gag {player} {12345678901234567890abcd:my_new_gag} #alone".. "
@w Then in your plugin with ID 12345678901234567890abcd,".. "
@w you could add a new my_new_gag function like:".. "
@w@C function my_new_gag()".. "
@w@C local line = CallPlugin("26240e292291e159e1ff3cde","current_line")".. "
@w@C if line:sub(1,15) == string.char(255,250,201).."comm.channel" then".. "
@w@C -- returning true says gag this if it's a gmcp comm.channel message".. "
@w@C return true".. "
@w@C else".. "
@w@C -- returning false or nil says don't gag".. "
@w@C return false".. "
@w@C end".. "
@w@C end".. "
@w@C".. "
@w As shown, using @CCallPlugin("26240e292291e159e1ff3cde", "current_line")@w".. "
@w in your handler function will return the message being processed.".. "
@Y"..string.rep(" ", (60-#header:gsub("begin","end"))/2)..header:gsub("begin","end"))) end
function listsub(name, line, wildcards) ColourNote("yellow","","") ColourNote("yellow",""," Current #subs:") ColourNote("yellow","","/-----------------------------------------------------------------------------\") for i,v in ipairs(subs) do AnsiNote(ColoursToANSI("@Y"..string.format(" %4u",i)..": "..format_sub(v))) end ColourNote("yellow","","\-----------------------------------------------------------------------------/") ColourNote("yellow","","") end
function listgag(name, line, wildcards) ColourNote("yellow","","") ColourNote("yellow",""," Current #gags:") ColourNote("yellow","","/-----------------------------------------------------------------------------\") for i,v in ipairs(gags) do AnsiNote(ColoursToANSI("@Y"..string.format(" %4u",i)..": "..format_gag(v))) end ColourNote("yellow","","\-----------------------------------------------------------------------------/") ColourNote("yellow","","") end
function find_in(set, original, nocase, alone, use_function) for i,v in ipairs(set) do if v.original == original and v.nocase == nocase and v.alone == alone and v.use_function == use_function then return i end end end
function remsub(name, line, wildcards, silent) local found_index = tonumber(wildcards[1]) local found = found_index and subs[found_index] or false
if not silent then Note() if found then AnsiNote(ColoursToANSI("@Ysub #"..found_index.." removed. Formerly: ["..format_sub(found).."]")) else ColourNote("yellow","","sub #"..tostring(wildcards[1]).." not found.") end end if found then table.remove(subs, found_index) set_triggers() SaveState() end end
function format_gag(gag) return ""@W"..gag.original:gsub("@","@@").."@Y""..(gag.nocase and " #nocase" or "")..(gag.alone and " #alone" or "").. (gag.use_function and (" @Y--> #function "@W"..gag.use_function:gsub("@","@@").."@Y"") or "") end
function format_sub(sub) return ""@W"..sub.original:gsub("@","@@").."@Y""..(sub.nocase and " #nocase" or "")..(sub.alone and " #alone" or "").. " @Y--> ".. (sub.use_function and "#function " or "")..""@W"..sub.tostr:gsub("@","@@").."@Y"" end
function remgag(name, line, wildcards, silent) local found_index = tonumber(wildcards[1]) local found = found_index and gags[found_index] or false
if not silent then Note() if found then AnsiNote(ColoursToANSI("@Ygag #"..found_index.." removed. Formerly: ["..format_gag(found).."]")) else ColourNote("yellow","","gag #"..tostring(wildcards[1]).." not found.") end end if found then table.remove(gags,found_index) set_triggers() SaveState() end end
function addsub(name, line, wildcards) local fromstr = wildcards[1] local tostr = wildcards[2] local nocase = wildcards[4] ~= "" local alone = wildcards[5] ~= "" local use_function = wildcards[6] ~= ""
if use_function then local s,e = tostr:find("%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x:%S*") if s ~= 1 or e ~= #tostr then AnsiNote(ColoursToANSI("
@YYou specified @W#function@Y, but pattern '@W"..tostr.."@Y' does not meet the format requirements.".. "
Please use {@W24_hexadecimal_digit_plugin_id:plugin_function_name@Y} as indicated in @W#sub help@Y.")) return end end
if nocase then fromstr = string.gsub(fromstr, '(%a)', function (v) return '['..string.upper(v)..string.lower(v)..']' end) end if alone then fromstr = "%f[^%c%z%s%p]"..fromstr.."%f[%c%z%s%p]" end if nocase or alone then ColourNote("yellow", "", "You specified"..(nocase and " #nocase" or "")..(alone and " #alone" or "")..", so converting your #sub patterns...") ColourNote("yellow", "", "") ColourNote("yellow", "", """..wildcards[1]..""") ColourNote("yellow", "", "becomes") ColourNote("yellow", "", """..fromstr..""") end
local found = find_in(subs, wildcards[1], nocase, alone, use_function) if found then remsub(nil, nil, {found}) end
table.insert(subs, { fromstr=fromstr, tostr=tostr, original=wildcards[1], nocase=nocase, alone=alone, use_function=use_function })
apply_pattern_as_trigger(wildcards[1], nocase, alone)
AnsiNote(ColoursToANSI("@YCreated sub "..#subs..": "..format_sub(subs[#subs]))) SaveState() end
function addgag(name, line, wildcards)
local gagstr = wildcards[1] local nocase = wildcards[3] ~= "" local alone = wildcards[4] ~= ""
local use_function = wildcards[5] ~= "" and wildcards[5] or nil
if use_function then local s,e = use_function:find("%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x%x:%S*") if s ~= 1 or e ~= #use_function then AnsiNote(ColoursToANSI("
@YYou specified @W#function@Y, but pattern '@W"..use_function.."@Y' does not meet the format requirements.".. "
Please use {@W24_hexadecimal_digit_plugin_id:plugin_function_name@Y} as indicated in @W#sub help@Y.")) return end end
if nocase then gagstr = string.gsub(gagstr, '(%a)', function (v) return '['..string.upper(v)..string.lower(v)..']' end) end if alone then gagstr = "%f[^%c%z%s%p]"..gagstr.."%f[%c%z%s%p]" end
if alone or nocase then ColourNote("yellow", "", "You specified"..(nocase and " #nocase" or "")..(alone and " #alone" or "")..", so converting your #gag pattern...") ColourNote("yellow", "", "") ColourNote("yellow", "", """..wildcards[1]..""") ColourNote("yellow", "", "becomes") ColourNote("yellow", "", """..gagstr..""") end
local found = find_in(gags, wildcards[1], nocase, alone, use_function) if found then remgag(nil, nil, {found}) end
table.insert(gags, { gagstr=gagstr, original=wildcards[1], nocase=nocase, alone=alone, use_function=use_function })
apply_pattern_as_trigger(wildcards[1], nocase, alone)
AnsiNote(ColoursToANSI("@YCreated gag "..#gags..": "..format_gag(gags[#gags]))) SaveState() end
function OnPluginSaveState() SetVariable("subs", serialize.save("subs")) SetVariable("gags", serialize.save("gags")) end
prev_text = "" EOL = "
"
function OnPluginDisconnect() OnPluginConnect() end
function OnPluginConnect() dosubs = false prev_text = "" end
function OnPluginListChanged () if IsPluginInstalled("26240e292291e159e1ff3cde") then ColourNote("yellow","red","You can't have two text substitution plugins active at the same time.") ColourNote("yellow","red","Remove the active one first before adding the other.") UnloadPlugin("26240e292291e159e1ff3cde") end end -- OnPluginListChanged
-- proxy_sub("26240e292291e159e1ff3cde:test") function proxy_sub(plugin_func_str, prev_color) local callstart = 'local a,b = CallPlugin("'..plugin_func_str:gsub(':', '","')..'"' return function(...) local params = {} for n=1,select('#',...) do params[#params+1] = '"'..select(n,...):gsub('"', '\"')..'"' end local func = callstart..(#params > 0 and (","..table.concat(params,",")) or "")..'); if a ~= 0 then Note("Error in sub: "..b) else return b and string.gsub(b,"%$C", "'..prev_color..'") end' return loadstring(func)() end end
function proxy_gag(plugin_func_str) local callstart = 'local a,b = CallPlugin("'..plugin_func_str:gsub(':', '","')..'"' return function(...) local params = {} for n=1,select('#',...) do params[#params+1] = '"'..select(n,...):gsub('"', '\"')..'"' end local func = callstart..(#params > 0 and (","..table.concat(params,",")) or "")..'); if a ~= 0 then Note("Error in gag: "..b) else return b end' return loadstring(func)() end end
function do_gags_against_coded_colors_table(msg_t) for i = 1,#msg_t,2 do local color_bit = msg_t[i] for _,v in ipairs(gags) do if color_bit:find(v.gagstr) then if not v.use_function then return true else return proxy_gag(v.use_function)() end end end end return false end
function do_subs_against_coded_colors_table(msg_t) for i = 1,#msg_t,2 do local color_bit = msg_t[i] for _,v in ipairs(subs) do local prev_color = msg_t[i-1] or "@x007" local replacement = v.use_function and proxy_sub(v.tostr, prev_color) or v.tostr:gsub("%$C", prev_color) local ok,ret = pcall(string.gsub, color_bit, v.fromstr, replacement) if not ok then AnsiNote(ColoursToANSI("@YError in sub ["..format_sub(v).."]: "..ret)) else color_bit = ret end end msg_t[i] = color_bit end return table.concat(msg_t) end
cur_line = "" function current_line() return cur_line end
function filter_trigger(name, line, wildcards, styles) local old_line = StylesToColours(styles) local new_line = filter_string(old_line) if new_line then EnableTriggerGroup("all", false) Simulate(ColoursToANSI(new_line).."
") EnableTriggerGroup("all", true) end end
-- returns nil if gagged, otherwise returns the modified message -- input: string with Aardwolf color codes function filter_string(msg) cur_line = msg
-- Matching #alone patterns at color and line boundaries is easier if -- we canonicalize the color codes first. local msg_t = canonicalize_colours(msg):split("%f[@]@x%d%d%d", true)
local gagged = do_gags_against_coded_colors_table(msg_t) if not gagged then return do_subs_against_coded_colors_table(msg_t) else return nil end end
